select pp.default_code as product_code,pp.name_template as Product,pg.name as generic_name,round(sum(iar.product_qty)) as Quantity ,pu.name as uom from stock_indent_analysis_report iar inner join indent_indent ii on ii.id=iar.indent_id and iar.state = 'received' and iar.approve_date between '#startDate#' AND '#endDate#' inner join stock_location sl on sl.id=iar.department_id and sl.name like '%Bedawal%' inner join product_product pp on pp.id=iar.product_id  inner join product_uom pu on pu.id = iar.uom_id left outer join product_generic pg on pg.id=pp.product_generic_id group by pp.name_template,pu.name,pp.default_code,pg.name order by  pp.name_template;
